    private void create_employeeMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_create_employeeMouseClicked
         try{
        Connection con = DriverManager.getConnection("jdbc:mariadb://localhost:3306/baseis", "root", "");
            
        String store = "INSERT INTO employee(employee_username,bio,sistatikes,certificates,awards,employees_firm,empl_exp_years) VALUES (?,?,?,?,?,?,?)";
                  
                 PreparedStatement em = con.prepareStatement(store);
                 em.setString(1, usr.getText());
                 em.setString(2, employee_bio.getText());
                 em.setString(3, reference.getText());
                 em.setString(4, certificates.getText());
                 em.setString(5, awards.getText());
                 em.setString(6, employee_comp_afm.getText());
                 em.setString(7, employee_exp.getText());
                 
             
                
                 ResultSet fdrd = em.executeQuery();
                 JOptionPane.showMessageDialog(null, "Employee created");
       }
       catch(SQLException e) {
          JOptionPane.showMessageDialog(null, e);
        }
    }//GEN-LAST:event_create_employeeMouseClicked

