    private void create_evaluatorMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_create_evaluatorMouseClicked
        try{
        Connection con = DriverManager.getConnection("jdbc:mariadb://localhost:3306/baseis", "root", "");
            
        String store = "INSERT INTO evaluator(evaluator_username,evaluator_exp_years,evaluator_firm,EvId) VALUES (?,?,?,?)";
                  
                 PreparedStatement ev = con.prepareStatement(store);
                 ev.setString(1, usereval.getText());
                 ev.setString(2, evaluator_exp.getText());
                 ev.setString(3, evaluator_comp_afm.getText());
                 ev.setString(4, EvId.getText());
             
                
                 ResultSet fdrv = ev.executeQuery();
                JOptionPane.showMessageDialog(null, "Evaluator created");
       }
       catch(SQLException e) {
          JOptionPane.showMessageDialog(null, e);
        }
    }//GEN-LAST:event_create_evaluatorMouseClicked

