    private void create_managerMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_create_managerMouseClicked
       try{
        Connection con = DriverManager.getConnection("jdbc:mariadb://localhost:3306/baseis", "root", "");
            
        String store = "INSERT INTO manager(manager_username,exp_years,firm) VALUES (?,?,?)";
                  
                 PreparedStatement ma = con.prepareStatement(store);
                 ma.setString(1, usrname.getText());
                 ma.setString(2, manager_exp.getText());
                 ma.setString(3, manager_comp_afm.getText());
             
                
                 ResultSet fdr = ma.executeQuery();
                 JOptionPane.showMessageDialog(null, "Manager created");
       }
       catch(SQLException e) {
          JOptionPane.showMessageDialog(null, e);
        }
    }//GEN-LAST:event_create_managerMouseClicked

