    private void jToggleButton1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jToggleButton1MouseClicked
       try{
        Connection con = DriverManager.getConnection("jdbc:mariadb://localhost:3306/baseis", "root", "");
            
        String store = "INSERT INTO company(afm,doy,company_name,phone,street,num,city,country) VALUES (?,?,?,?,?,?,?,?)";
                  
                 PreparedStatement em = con.prepareStatement(store);
                 em.setString(1, afm.getText());
                 em.setString(2, doy.getText());
                 em.setString(3, name.getText());
                 em.setString(4, phone.getText());
                 em.setString(5, street.getText());
                 em.setString(6, num.getText());
                 em.setString(7, city.getText());
                 em.setString(8, country.getText());
                 
             
                
                 ResultSet fdrd = em.executeQuery();
                 JOptionPane.showMessageDialog(null, "Company created");
       }
       catch(SQLException e) {
          JOptionPane.showMessageDialog(null, e);
        }
    }//GEN-LAST:event_jToggleButton1MouseClicked

