    private void jToggleButton1ActionPerformed(java.awt.event.ActionEvent evt) {                                               
        // TODO add your handling code here:
   try{
            Connection con = DriverManager.getConnection("jdbc:mariadb://localhost:3306/baseis", "root", "");
            
            String check = "SELECT * FROM user WHERE username=? AND password=?";
            PreparedStatement pst = con.prepareStatement(check);
            pst.setString(1, Username.getText());
            pst.setString(2, Password.getText());
            
            ResultSet rs = pst.executeQuery();
            
            if (rs.next()) {
                JOptionPane.showMessageDialog(null, "Username and Password Matched!");
                
                String manager = "SELECT manager_username FROM manager WHERE manager_username = ?";
                PreparedStatement man = con.prepareStatement(manager);
                man.setString(1, Username.getText());
                ResultSet manag = man.executeQuery();
            
                if (manag.next()){
                    JOptionPane.showMessageDialog(null, "You are a manager");
                    java.awt.EventQueue.invokeLater(new Runnable() {
                    public void run() {
                    new manager().setVisible(true);
                     }
                    });
                
                }
                else{
                    String evaluator= "SELECT evaluator_username FROM evaluator WHERE evaluator_username = ?";
                    PreparedStatement eval = con.prepareStatement(evaluator);
                    eval.setString(1, Username.getText());
                    ResultSet evaluat = eval.executeQuery();
            
                    if (evaluat.next()){
                        JOptionPane.showMessageDialog(null, "You are an evaluator");
                
                
                    }
                    else{
                        String employee = "SELECT employee_username FROM employee WHERE employee_username = ?";
                        PreparedStatement empl = con.prepareStatement(employee);
                        empl.setString(1, Username.getText());
                        ResultSet emplo = empl.executeQuery();
            
                        if (emplo.next()){
                            JOptionPane.showMessageDialog(null, "You are an employee");
                
                
                        }
                        else{
                            
                            String admin = "SELECT username FROM user WHERE username = ? AND is_admin = 'yes' ";
                            PreparedStatement adm = con.prepareStatement(admin);
                            adm.setString(1, Username.getText());
                            ResultSet ad = adm.executeQuery();
            
                                if (ad.next()){
                                    JOptionPane.showMessageDialog(null, "You are an admin");
           
                                    java.awt.EventQueue.invokeLater(new Runnable() {
                                     public void run() {
                                        new admin().setVisible(true);
                                     }
                                    });
            
                                }
                                else {
                                JOptionPane.showMessageDialog(null, "Pepega");
                                }
                            }
                        }
            
                    }
            }
            else {
                JOptionPane.showMessageDialog(null, "Sorry, Try Again...");
            }
            
            
        }
        catch(SQLException e) {
            JOptionPane.showMessageDialog(null, e);
        }
          
   
    }  
